#include "Day1.h"
#include <string>

const std::string input{
    ""
};

int Day1::Part1() {
    int total = 0;
    int a = 0;
    int b = 1;
    int c = 3;

    int length = input.length();

    for (int i = 0; i < length; i++) {
        char current = input[i];
        switch (current) {
            case 'B':
                total += b;
                break;
            case 'C':
                total += c;
                break;
            default:
                total += a;
        }
    }
    return total;
}
